    signal RstOutI : std_logic;
    signal VldOut  : std_logic;

begin

    p_vldgen : process (In_Clk) is
    begin
        if rising_edge(In_Clk) then
            VldIn <= VldFb;

            if (Started = '0') then
                VldIn   <= '1';
                Started <= '1';
            end if;

            if RstInI = '1' then
                RstOutI_Sync <= (others => '1');
                Started      <= '0';
                VldIn        <= '0';
            end if;
        end if;
    end process;

    i_scc : entity work.olo_base_cc_simple
        generic map (
            Width_g      => Width_g,
